function init(){var i=$(document).width();$(".step-pic").css("width",i),$(".step-pic ol li").css("width",i),$(".step-pic ol").css("width",3*i)}function setPicIndex(i){$(".step-pic ol").css("left",-i*$(document).width()),$(".header").text(i+1+"/3")}$(function(){init();var i=parseInt(location.hash.slice(1));setPicIndex(i-1),$(".big-pic").hammer().on("swipeleft swiperight",function(t){"swipeleft"===t.type&&3>i&&(i+=1,setPicIndex(i-1)),"swiperight"===t.type&&i>1&&(i-=1,setPicIndex(i-1))})});