function init(i){$(".step-pic").css("width",i),$(".step-pic ol li").css("width",i),$(".step-pic ol").css("width",3*i)}function setPicIndex(i,t){$(".step-pic ol").css("left",-i*t),$(".header").text(i+1+"/3")}$(function(){var i=$(document).width();init(i);var t=parseInt(location.hash.slice(1));setPicIndex(t-1,i),$(".big-pic").hammer().on("swipeleft swiperight",function(e){"swipeleft"===e.type&&3>t&&(t+=1,setPicIndex(t-1,i)),"swiperight"===e.type&&t>1&&(t-=1,setPicIndex(t-1,i))})});