function init(t){$(".step-pic").css("width",t),$(".step-pic ol li").css("width",t),$(".step-pic ol").css("width",3*t)}function setPicIndex(t,i){$(".step-pic ol").css("transform","translateX("+-t*i+"px)"),$(".header").text(t+1+"/3")}$(function(){var t=$(document).width();init(t);var i=parseInt(location.hash.slice(1));setPicIndex(i-1,t),$(".big-pic").hammer().on("swipeleft swiperight",function(e){"swipeleft"===e.type&&3>i&&(i+=1,setPicIndex(i-1,t)),"swiperight"===e.type&&i>1&&(i-=1,setPicIndex(i-1,t))})});